$(document).ready(function () {
    $(".SelectFilter").on("change", function () {
        var e = $(this).data("target"),
            i = $(this).find(":selected").data("ref");
        $("select." + e).val("-1"),
            null == i ? $("select." + e).find("option").each(function () {
                console.log("inside undefined"), $(this).removeAttr("disabled hidden")
            }) : $("select." + e).find("option").each(function () {
            var e = $(this).data("belong"), t = $(this).val();
            i != e && -1 != t ? ($(this).prop("disabled", !0), $(this).prop("hidden", !0)) : ($(this).prop("disabled", !1), $(this).prop("hidden", !1))
            })
    });
});


$(document).ready(function () {
    var client = $("#client");
    var vehicle = $("#vehicle");
    var selectedVeh = vehicle.find(":selected").val();

    var e = client.data("target"),
        i = client.find(":selected").data("ref");
    $("select." + e).val("-1"),
        null == i ? $("select." + e).find("option").each(function () {
            console.log("inside undefined"), $(this).removeAttr("disabled hidden")
        }) : $("select." + e).find("option").each(function () {
            var e = $(this).data("belong"), t = $(this).val();
            i != e && -1 != t ? ($(this).prop("disabled", !0), $(this).prop("hidden", !0)) : ($(this).prop("disabled", !1), $(this).prop("hidden", !1))
        })
    vehicle.val(selectedVeh);
});

$(document).ready(function () {
    $(".formElement").on("change", function () {
        var desc = document.getElementById("description");
        var textVeh = $("#vehicle :selected").val();
        var textCli = $("#client :selected").val();

        if (textCli != -1 && textVeh != -1) {
            $("#description").removeAttr("disabled");
            $("#result").removeAttr("disabled");
        }
        else {
            $("#description").prop("disabled", !0);
            $("#result").prop("disabled", !0);
        }

        var attr = $("#description").attr('disabled');
        if ((typeof attr == 'undefined' || attr == false) && ($("#description").val() != "")) {
            $("#addAction").removeAttr("disabled");
            $("#saveButton").removeAttr("disabled");
        }
        else {
            $("#addAction").prop("disabled", !0);
            $("#saveButton").prop("disabled", !0);
        }
    });
});




